package kont2011;public class Rating {		private double pointSum = 1, weightSum = 1;		public void receivePoints(int points, Person rater) {		if (points < 1 || points > 5) {			throw new IllegalArgumentException("points must be between 1 and 5, but was " + points);		}		double raterWeight = rater.getRating();		pointSum += raterWeight * points;		weightSum += raterWeight;	}		public double getRating() {		return pointSum / weightSum;	}}